		<?php				include("/connect/mysql_connect.php");					$query=mysql_query("SELECT distinct(brand) as b from inventory where brand!='' order by b");				echo "<option value=\"\"></option>";				while ($r=mysql_fetch_assoc($query)) {	    	$b=$r['b'];	    				$selected=$b==$brand?"selected=\"selected\"":"";					echo "<option value=\"$b\" $selected\">$b</option>";		}						?>																						