		<?php				include("/connect/mysql_connect.php");					$query=mysql_query("SELECT DISTINCT CONCAT(ucase(pattern),\" BY \",IF(brand<>\"\",ucase(brand),\"UNKNOWN\")) as p,ucase(pattern) as pvalue FROM inventory WHERE quantity!=0 AND pattern!=\"\" ORDER BY pattern,brand");		echo "<option value=\"\"></option>";		echo "<option value\"NON\">NON-PATTERNED</option>";		while ($r=mysql_fetch_assoc($query)) {	    	$p=$r['p'];			$pvalue=$r['pvalue'];			$selected=$pvalue==urldecode($pattern)?"selected=\"selected\"":"";			echo "<option value=\"$pvalue\" $selected>$p</option>";		}						?>																						