<?function ftp_rmAll($cId,$dir) {	$files=ftp_nlist($cId,$dir);	foreach($files as $st) { 		if (($st==".")  || ($st==".." )) {continue;} 			if (substr($st,-3)!='php') {				ftp_rmAll($cId,"$dir/$st");				}			else { echo "delete $st<BR>";				ftp_delete($cId,"$dir/$st");				}			} 	echo "remove $dir<BR>";	ftp_rmdir($cId,$dir);	}	if (!$conn_id = @ftp_connect('69.36.171.242')) { die("FTP connection failed."); } /*login with username and password*/if (!$login_result = @ftp_login($conn_id,'asyoulikeitsilvershop','Dia4mf')) { die("FTP LOG IN FAILED"); }ftp_pasv($conn_id,TRUE); $insertNum=1;include('/connect/mysql_connect.php');$style=array('f'=>'_tan','h'=>'','bs'=>'_blue','j'=>'_jewelry','cp'=>'','stp'=>'','c'=>'');$aylissGraphic=array('f'=>'t','h'=>'r','bs'=>'b','j'=>'b','cp'=>'r','stp'=>'r');$titles=array('f'=>'t_flat_main_alt','h'=>'t_hol_main_alt','bs'=>'t_baby_main_alt','j'=>'t_jew_main_alt','cp'=>'t_caring','stp'=>'t_caring');$onLoad=array('f'=>"'flata'",'h'=>"'hola'",'bs'=>"'bsa'",'j'=>"'jewa'",'cp'=>'','stp'=>'');$keyCat=array('f'=>'Flatware','h'=>'Hollowware','bs'=>'Baby Silver','j'=>'Jewelry','cp'=>'SilverCare','stp'=>'SilverStorage');$template=array('f'=>'f','h'=>'h','bs'=>'bs','j'=>'j','cp'=>'cp','stp'=>'stp');$where=($id)?"id=$id":"category!=''";$q=mysql_query("SELECT * from inventory WHERE productId=$productId");/*$q=mysql_query("SELECT * from inventory where $where order by category ");*//*echo mysql_num_rows($q);//exit;*/$path="/home/asyoulik/public_html/staticHTML/";while ($row=mysql_fetch_assoc($q)) {set_time_limit(120);extract($row);$category=strtolower($category);if ($category=='sp' || $category=='fcs' || $category=='ps') { $category='f';}if (!$keyCat[$category])  {continue; }if (!is_dir($path.$keyCat[$category])) {	ftp_mkdir($conn_id,$path.$keyCat[$category]);	//$chmod_cmd="CHMOD 0777 $path$keyCat[$category]"; 	}if (!is_dir($path.$keyCat[$category]."/_$brand")) {	ftp_mkdir($conn_id,$path.$keyCat[$category]."/_$brand"); 	//$chmod_cmd="CHMOD 0777 $path.$keyCat[$category]"; 	}if (!is_dir($path.$keyCat[$category]."/_$brand/_$pattern")) {	ftp_mkdir($conn_id,$path.$keyCat[$category]."/_$brand/_$pattern"); 	//$chmod_cmd="CHMOD 0777 $path$keyCat[$category]"; 	}$ubrand=strtolower($brand);$uitem=strtolower($item);$upattern=strtolower($pattern);$ucat=strtolower($keyCat[$category]);$content="<? \$f=file_get_contents('/home/asyoulik/public_html/includes/staticheader.inc.html',\$f);\$menu=file_get_contents('/home/asyoulik/public_html/silver_cats.in.php');\$f=str_replace('<!--silver_cats-->',\$menu,\$f);\$f=str_replace('<!--cs-->','{$style[$category]}',\$f);\$f=str_replace('<!--title-->','{$titles[$category]}',\$f);\$f=str_replace('<!--aylissgraphic-->','{$aylissGraphic[$category]}',\$f);\$f=str_replace('<!--onLoad-->',\"{$onLoad[$category]}\",\$f);\$f=str_replace('<!--keywords-->',\"$item,$brand,$pattern,{$keyCat[$category]}\",\$f);\$f=str_replace('<!--category-->',\"{$keyCat[$category]}\",\$f);echo \$f;?>";$re=array("&",",",'.','#','by','BROTHERS','INTERNATIONAL');$rw=array("AND",'','','','','BROS','INTL');		$folder=strtoupper(substr($row[pattern],0,1));if ($row[pattern] && $row[brand]) { $title=$row[pattern]." by ".$row[brand]; }else {  $title=$row[pattern].$row[brand]."&nbsp;"; }$utitle=strtolower($title);$instock=($template[$category]=='f')?(($row[quantity]>-1)?$row[quantity]:'16'):'yes';if (!$row[image] || !file_exists("../productImages/_BG/$row[image]")) { 			$handle="HANDLES/$folder/".str_replace($re,$rw,strtoupper("$row[pattern] $row[brand]")).".jpg";		$row[image]=($row[handle])? $handle:'/productImages/_TN/noimage_th.jpg'; 	$imageS=(file_exists("../$handle"))? "src='/$handle' alt=\"$utitle $uitem, sterling silver,silverware $ucat\" width=300><br><br><font style=font-size:12px>Sorry, there is no product image available for this item.<br>We have included a picture of the pattern to assist you.</font>" :"src='/productImages/_SM/noimage.jpg' \"alt='$utitle $uitem, sterling silverware, sterling flatware, sterling hollowware'\" width=250>"; } 	else {	$isize= getImageSize("../productImages/_BG/".$row[image]);	$imageS="src='/productImages/_SM/".substr($row[image],0,-4)."_SM.jpg' width=250 onClick=\"enlarge('$row[image]',{$isize[0]},{$isize[1]},'$row[item]')\"  alt='$utitle $uitem. Click to enlarge image'><br><font style=font-size:10px>To view an enlarged image, click on the image above.<font>";	}if ($row[sale]) {$price=$row[sale]; }  else {$price=$row[retail]; }$monogram=($row[monogram])?"(monogrammed)":'';$content.="<form name='itemsForm'><table width='760' cellpadding='0' cellspacing='0' border='0' align='left'><tr>	<td width='50'><img src=\"manage/images/blank.gif\" width='50' height='1' alt='' border='0'></td>	<td valign='top' width='300' align=left><img $imageS</td><td width='10'><img src=\"manage/images/blank.gif\" width='10' height='1' alt='' border='0'></td>	<td valign='top' width='400'><h5>$title</h5><p>$row[item] $monogram<br>".(stripslashes($row[dimension]))."<br>";if ($row[weight]) { $content.="$row[weight] troy oz."; }$content.="</p>	<table border='0' align='center' cellpadding='2' cellspacing='2' width=100%>		<tr>		<td colspan=3>".stripslashes($row[description])."</td>	</tr>	<tr>		<td  nowrap><b class='category'>Price:</b></td> 	<td  align=left >\$$price</td>	<td width=300>&nbsp;</td>	</tr>	<tr>		<td colspan='3'><img src=\"manage/images/blank.gif\" width='5' height='1' alt='' border='0'></td>	</tr>	<tr>		<td  nowrap><b class='category'>In Stock:</b></td> 		<td>$instock</td>	<td width=300>&nbsp;</td>	</tr>	<tr>		<td  valign=bottom><input type='button' value='Add' onClick=\"javascript:location='/addItem.php?id=$row[id]&quantity='+this.form.quantity$row[id].value+'&temp=$template[$category]'\">&nbsp;<input type='text' value='1' name=quantity$row[id] size=2></td>		<td><img src='/images/silverchest_add.gif' name='chestimage$row[id]' onClick=\"updateCart($row[id],1);\" align=bottom width=42></td>		<td width=300>&nbsp;</td>	</tr>	</table></td></tr></table></form>";$content.='<br clear="all"><table width="760" cellpadding="0" cellspacing="0" border="0" align="left"><tr>	<td width="50"><img src="manage/images/blank.gif" width="50" height="1" alt="" border="0"></td>	<td valign="top">	<hr width=710 noshade size=1 class=mainsub align=center>		<p class="bottom">&copy; Copyright 2003-'.date('Y').'. As You Like It Silver Shop.</p>		<p>&nbsp</p>	</td></tr></table></body></html>';$f=fopen('temp/temp.html','w');fputs($f,$content);fclose($f);echo $insertNum+'. '+$path.$keyCat[$category]."/_$brand/_$pattern/{$item}_$id<BR>";$fname="/_".str_replace(array('/'),array(''),$brand)."/_".str_replace(array('/'),array(''),$pattern)."/".str_replace(array('/'),array(''),$item)."_$id";ftp_put($conn_id, $path.$keyCat[$category].$fname, 'temp/temp.html', FTP_ASCII);$insertNum+=1;}?>