<?phpinclude("/connect/mysql_connect.php");/*	*/function createFileName($path,$v,$pattern,$brand){		$brand=str_replace(" ", "-",$brand);				if($pattern!=""){		$pattern=str_replace("#","",$pattern);			$pattern=str_replace(" ","-",$pattern);			$fname=strtolower(str_replace(array('/'),array(''),$pattern))."-by-".strtolower(str_replace(array('/'),array(''),$brand));		}		else{			$fname=strtolower(str_replace(array('/'),array(''),$brand));				}					$fname=str_replace(' ', '', $fname);		$fname=str_replace("'",'',$fname);		$fname=str_replace('&','and',$fname);		$fname=str_replace('co.','company',$fname);		$fname=str_replace('co ','company',$fname);		$fname=str_replace('bros.','brothers',$fname);		$fname=str_replace('bro ','brothers',$fname);		$fname=str_replace('.','',$fname);		$fname=str_replace(',','',$fname);				$keyword="";		if($v!="SilverCare" && $v!="SilverStorage"){			$keyword="-sterling-silver";		}						$fname=$path.$v."/".$fname."$keyword.html";		return $fname;	}function rewriteSearchURL($pattern,$brand,$searchCategory,$category,$recent,$item,$template){	$searchURL="href=http://www.asyoulikeitsilvershop.com/showSearch/template/$template/";	if($category!=""){$searchURL.="/category/$category";}	if($brand!=""){$searchURL.="/brand/".urlencode($brand);}	if($pattern!=""){$searchURL.="/pattern/".urlencode($pattern);}	if($recent!=""){$searchURL.="/recent/$recent";}	if($searchCategory!=""){$searchURL.="/searchCategory/$searchCategory";}	if($item!=""){$searchURL.="/item/";}		return $searchURL;}function staticURL($pattern,$brand,$filecat,$category,$item,$id){//if($pattern==""){$pattern="UNKNOWN";}//if($brand==""){$brand="UNKNOWN";}//$sURL="/staticHTML/".$category."/".str_replace(array('/'),array('-'),$brand)."/".str_replace(array('/'),array('-'),$pattern)."/";if($pattern!=""){//$sURL.=strtolower(str_replace(array('/'),array(''),$pattern))."by";}$keyword="STERLING-SILVER-";if($category=='cp' || $category=='stp' || $category==''){	$keyword="";}//$sURL.=$keyword.rawurlencode(strtolower(str_replace(array('/'),array('-'),$item)))."-".$id;$sURL="/staticHTML/$filecat/_".str_replace(array('/'),array(''),$brand)."/_".str_replace(array('/'),array(''),$pattern)."/".$keyword.rawurlencode(str_replace(array('/'),array(''),$item))."-$id";return $sURL;}if($_POST || $_GET){	extract($_POST);	extract($_GET);		//echo "$brand $pattern $item $category";$w="WHERE quantity !=0";if($pattern || $brand || $category || $item || $minretail || $maxretail){	if($pattern && $pattern!="NON-PATTERNED"){		$w.=" AND pattern=\"$pattern\"";	}	if($brand){		$w.=" AND brand=\"$brand\"";	}	if($category){		$w.=" AND category=\"". strtolower($category) ."\"";	}	if($item){		if(strchr($item, "O CLOCK")>0){ 		str_replace($item, "O ", "O'");	}	$w.=" AND (item=\"".urldecode($item)."\" OR instr(item,\"$item\")>0 or item LIKE \"$item\")";}if($minretail){$w.=" AND retail>=$minretail";}if($maxretail){$w.=" AND retail <=$maxretail";}$bgs=array('f'=>'#ffeedd','h'=>'#ffdddd','bs'=>'#dedeff','gift'=>'#dedeff','m'=>'#ffeedd','s'=>'#ffdddd','xm'=>'#ddffdd','j'=>'#6699CC');$staticcats=array('sp'=>'Flatware','fcs'=>'Flatware','f'=>'Flatware','ps'=>'Flatware','h'=>'Hollowware','bs'=>'Baby Silver','j'=>'Jewelry','stp'=>'SilverStorage','cp'=>'SilverCare','xm'=>'Christmas');$keyCat=array(''=>'All','sp'=>'Serving Pieces','ps'=>'Flatware','fcs'=>'Complete Sets','f'=>'Flatware','h'=>'Hollowware','bs'=>'Baby Silver','j'=>"Jewelry",'cp'=>'SilverCare','stp'=>'SilverStorage','xm'=>'Christmas');$query="SELECT * FROM inventory $w ORDER BY item,brand,pattern";//echo $query;$result=mysql_query($query);      $c="   		<table width=\"740\" cellpadding=\"0\" style=\"background-color:transparent;\">";   	$c.="<tr>   			<td></td>   			<td></td>   			<td></td>   			<td></td>   			<td>Item</td>   			<td>Retail</td>   			<td width=\"40\" nowrap=\"true\">In Stock</td>   			<td colspan=\"2\"></td>   		</tr>";   		    if(mysql_numrows($result)>0){        	$numrows=mysql_numrows($result);  	while($row=mysql_fetch_assoc($result)){		$row[dimension]=str_replace("\\",'',$row[dimension]); 		$row[category]=strtolower($row[category]);		$instock=abs($row[quantity]);;		$price=($row[sale])?$row[sale]:$row[retail];			if (!$row[image] || !file_exists("productImages/_BG/$row[image]")) { 			$handle="/HANDLES/".strtoupper(substr($row[pattern],0,1))."/".strtoupper($row[pattern])." ".strtoupper($row[brand]).".jpg";			$testhandle="/home/asyoulik/public_html".$handle;			$row[image]=(file_exists($testhandle))? $handle:'/productImages/_TN/noimage_th.jpg'; 		}  		 else {			 $row[image]='/productImages/_TN/'.substr($row[image],0,-4)."_TN.jpg"; 		}				$monogram=$row[monogram]?"(monogrammed)":'';				$statURL = staticURL($row[pattern],$row[brand],$staticcats[$row[category]],$row[category],$row[item],$row[id]); //	<td $bgcolor ><p class=$class><b><a href='/staticHTML/{$staticcats[$row[category]]}/_".(str_replace(array('/'),array(''),$row[brand])."/_".str_replace(array('/'),array(''),$row[pattern])."/".rawurlencode(str_replace(array('/'),array(''),$row[item])))."_$row[id]' class=$class>$row[item] </a>$monogram</b></p></td><td $bgcolor><p class=$class><b>$row[dimension]".(($row[weight])?"<br>$row[weight] troy oz":'')."</b></td>";//	<td $bgcolor ><p class=$class><b><a href='/staticHTML/{$staticcats[$row[category]]}/_".(str_replace(array('/'),array(''),$row[brand])."/_".str_replace(array('/'),array(''),$row[pattern])."/".rawurlencode(str_replace(array('/'),array(''),$row[item])))."_$row[id]' class=$class>$row[item] </a>$monogram</b></p></td><td $bgcolor><p class=$class><b>$row[dimension]".(($row[weight])?"<br>$row[weight] troy oz":'')."</b></td>";                //$bgcolor=$bgcolor=="style=\"background-color:#FFFFFF\""?"style=\"background-color:#dddddd\"":"style=\"background-color:#FFFFFF\"";						$pb=$row[pattern]?"$row[pattern] by $row[brand]":$row[brand];		$wt=($row[weight])?"$row[weight] troy oz":'';				//<td $bgcolor><p class=$class><b>$row[dimension]".(($row[weight])?"<br>$row[weight] troy oz":'')."</b></td>		$c.= "<tr $bgcolor>			<td width='10'><img src='/images/blank.gif' width='10' height='1'></td>			<td width='100' height=100 align='center'><a href=\"$statURL\" style=\"text-decoration:none\" class=$class><img src='{$row[image]}' border=0 width=75><br><font size=-2>(click for details)</font></a></td>			<td width='10'><img src='/images/blank.gif' width='10' height='1'></td>			<td width='10' $bgcolor><img src='/images/blank.gif' width='10' height='1'></td>			<td $bgcolor><p class=$class><b><a href=\"$statURL\" class=$class>$pb $row[item]</a></b><br><span style=\"font-size:12px\">$monogram $row[dimension] $wt</span></p></td>";						//<td $bgcolor><p class=$class><b>$row[dimension]".(($row[weight])?"<br>$row[weight] troy oz":'')."</b></td>";				$brandfname="nofile.html";		$patternfname="nofile.html";		$pattern=$row[pattern];		$brand=$row[brand];		$item=str_replace("/","", $row[item]);		$category=$row[category];				if($row[brand]!=""){			$brandfname=createFileName("search/","All","",$row[brand]);		}				if($row[pattern]!=""){			$patternfname=createFileName("search/","All",$row[pattern],$row[brand]);		}			if(file_exists("/home/asyoulik/public_html/$brandfname")){			$bfilelink="http://www.asyoulikeitsilvershop.com/$brandfname";		}				else{		 	$bfilelink= rewriteSearchURL($pattern,$brand,$searchCategory,$category,$recent,$item); //"href=http://www.asyoulikeitsilvershop.com/showSearch/template/m/brand/$b"		 	//$bfilelink="href=http://www.asyoulikeitsilvershop.com/showProducts.php?searchCategory=$searchCategory&recent=$recent&brand=".urlencode($row[brand])."&category=$category&template=m";		}				if(file_exists("/home/asyoulik/public_html/$patternfname")){			 $pfilelink="http://www.asyoulikeitsilvershop.com/$patternfname";		}		else{			$pfilelink=rewriteSearchURL($pattern,$brand,$searchCategory,$category,$recent,$item);			//$pfilelink="http://www.asyoulikeitsilvershop.com/showProducts.php?searchCategory=$searchCategory&recent=$recent&pattern=".urlencode($row[pattern])."&category=$category&template=s";		}				/*$c.="				<td $bgcolor><p><b><a href=\"$pfilelink\">$row[pattern]</a></b></p></td>  			<td $bgcolor><p><b><a href=\"$bfilelink\">$row[brand]</a></b></p></td>";		*/		$c.="			<td $bgcolor ><p class=$class><b>\$$price</b></p></td>			<td $bgcolor align=center>$instock</td>			<td $bgcolor ><input type='text' value='1' size=2 name=quantity$row[id] id=\"$row[id]\"></td>			<td $bgcolor valign=\"center\" align=right><p class=$class><b><form><input type='button' value='Add to Registry' onclick=\"javascript:additem($row[id],0,0,1)\"></form></b></p></td>			</tr>";					}		}//<td $bgcolor valign=\"center\" align=right><p class=$class><b><input type='button' value='Add' onClick=\"javascript:location='addItem.php?id=$row[id]&quantity='+this.form.quantity$row[id].value+'&temp=$template'\">&nbsp;</b></p></td>else{	$c.="<tr><td>No Results found</td></tr>";}$c.="	</tbody>	</table>";	echo $c;}}?>